import React, { useState, useCallback, useRef } from 'react';
import { useDispatch } from 'react-redux';
import { Topic as TopicType, TopicType as TopicTypeEnum } from '../types/MindMap';
import {
  toggleTopicComplete,
  connectTopics,
  addTopic,
  updateTopicTitle,
  toggleTopicExpand,
  updateTopicType
} from '../store/mindMapSlice';
import { countIncompleteTopics, showNotification } from '../utils/topicUtils';

interface TopicProps {
  topic: TopicType;
  level?: number;
}

const Topic: React.FC<TopicProps> = ({ topic, level = 0 }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(topic.title);
  const [showTypeMenu, setShowTypeMenu] = useState(false);
  const topicRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  const handleComplete = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation();
    dispatch(toggleTopicComplete({
      id: topic.id,
      isCompleted: e.target.checked
    }));

    // 只在取消完成状态时，且有未完成的子任务时显示提示
    if (!e.target.checked && topic.children.length > 0) {
      const incompleteCount = countIncompleteTopics(topic);
      if (incompleteCount > 0) {
        showNotification(`还有${incompleteCount}个子主题未完成`);
      }
    }
  }, [dispatch, topic.id]);

  const handleAddSubTopic = () => {
    dispatch(addTopic({
      title: '新节点',
      parentId: topic.id
    }));
  };

  const handleTitleClick = () => {
    setIsEditing(true);
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditTitle(e.target.value);
  };

  const handleTitleBlur = () => {
    if (editTitle.trim() !== '') {
      dispatch(updateTopicTitle({
        id: topic.id,
        title: editTitle.trim()
      }));
    } else {
      setEditTitle(topic.title);
    }
    setIsEditing(false);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleTitleBlur();
    }
  };

  const handleDragStart = useCallback((e: React.DragEvent) => {
    e.stopPropagation();
    setIsDragging(true);
    e.dataTransfer.setData('text/plain', topic.id);
    e.dataTransfer.effectAllowed = 'move';

    if (topicRef.current) {
      const rect = topicRef.current.getBoundingClientRect();
      e.dataTransfer.setDragImage(topicRef.current, e.clientX - rect.left, e.clientY - rect.top);
    }
  }, [topic.id]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer.types.includes('text/plain')) {
      e.currentTarget.classList.add('drag-over');
      e.dataTransfer.dropEffect = 'move';
    }
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');

    const sourceId = e.dataTransfer.getData('text/plain');
    if (sourceId !== topic.id) {
      dispatch(connectTopics({
        sourceId,
        targetId: topic.id
      }));
    }
  }, [dispatch, topic.id]);

  const handleDragEnd = useCallback(() => {
    setIsDragging(false);
  }, []);

  const handleToggleExpand = (e: React.MouseEvent) => {
    e.stopPropagation();
    dispatch(toggleTopicExpand(topic.id));
  };

  const handleTypeChange = (type: TopicTypeEnum) => {
    setShowTypeMenu(false);
    dispatch(updateTopicType({ id: topic.id, type }));
  };

  const handlePaste = async (e: React.ClipboardEvent) => {
    if (topic.type !== 'image') return;

    const items = e.clipboardData.items;
    const imageItem = Array.from(items).find(item => item.type.startsWith('image'));

    if (imageItem) {
      e.preventDefault();
      const blob = imageItem.getAsFile();
      if (!blob) return;

      // 将图片转换为 base64
      const reader = new FileReader();
      reader.onload = (event) => {
        if (event.target?.result) {
          dispatch(updateTopicType({
            id: topic.id,
            type: 'image',
            content: event.target.result as string
          }));
        }
      };
      reader.readAsDataURL(blob);
    }
  };

  const handleImageDrop = (e: React.DragEvent) => {
    if (topic.type !== 'image') return;

    e.preventDefault();
    e.stopPropagation();

    const file = e.dataTransfer.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        if (event.target?.result) {
          dispatch(updateTopicType({
            id: topic.id,
            type: 'image',
            content: event.target.result as string
          }));
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const renderTopicContent = () => {
    if (topic.type === 'image' && topic.content) {
      return (
        <div className="topic-content">
          <span className="topic-title" onClick={handleTitleClick}>
            {topic.title}
          </span>
          <div className="topic-image">
            <img src={topic.content} alt={topic.title} />
          </div>
        </div>
      );
    }

    return (
      <span className="topic-title" onClick={handleTitleClick}>
        {topic.title}
      </span>
    );
  };

  return (
    <div
      ref={topicRef}
      className={`topic ${topic.type} ${topic.isCompleted ? 'completed' : 'incomplete'} ${isDragging ? 'dragging' : ''}`}
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={(e) => {
        if (e.dataTransfer.files.length > 0) {
          handleImageDrop(e);
        } else {
          handleDrop(e);
        }
      }}
      onDragEnd={handleDragEnd}
      onPaste={handlePaste}
      style={{ marginLeft: `${level * 20}px` }}
    >
      <div className="topic-header">
        <div className="topic-controls">
          {topic.children.length > 0 && (
            <button
              className="expand-btn"
              onClick={handleToggleExpand}
              title={topic.isExpanded ? '折叠' : '展开'}
            >
              {topic.isExpanded ? '−' : '+'}
            </button>
          )}
          <input
            type="checkbox"
            checked={topic.isCompleted}
            onChange={handleComplete}
          />
        </div>
        {isEditing ? (
          <input
            type="text"
            value={editTitle}
            onChange={handleTitleChange}
            onBlur={handleTitleBlur}
            onKeyPress={handleKeyPress}
            autoFocus
            className="topic-title-input"
          />
        ) : (
          renderTopicContent()
        )}
        <div className="topic-tools">
          <button
            className="type-btn"
            onClick={() => setShowTypeMenu(!showTypeMenu)}
            title="更改类型"
          >
            <span className="icon">⚙</span>
          </button>
          <button
            className="add-subtopic-btn"
            onClick={handleAddSubTopic}
            title="添加子节点"
          >
            <span className="icon">+</span>
          </button>
        </div>
      </div>

      {showTypeMenu && (
        <div className="type-menu">
          <button onClick={() => handleTypeChange('text')}>文本</button>
          <button onClick={() => handleTypeChange('image')}>图片</button>
        </div>
      )}

      {topic.type === 'image' && (
        <div className="content-input">
          <div className="image-upload-hint">
            可以直接粘贴或拖放图片到这里
          </div>
          {!topic.content && (
            <div className="image-placeholder">
              <span>点击或拖放图片到此处</span>
            </div>
          )}
        </div>
      )}

      {topic.isExpanded && topic.children.length > 0 && (
        <div className="topic-children">
          {topic.children.map(child => (
            <Topic key={child.id} topic={child} level={level + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

export default React.memo(Topic);
