import React from 'react';
import { useDispatch } from 'react-redux';
import { Topic as TopicType } from '../types/MindMap';
import { toggleTopicComplete } from '../store/mindMapSlice';
import { countIncompleteTopics, showNotification } from '../utils/topicUtils';

interface TopicProps {
  topic: TopicType;
}

const Topic: React.FC<TopicProps> = ({ topic }) => {
  const dispatch = useDispatch();

  const handleComplete = () => {
    dispatch(toggleTopicComplete(topic.id));

    // 计算未完成的子主题数量
    const incompleteCount = countIncompleteTopics(topic);
    if (incompleteCount > 0) {
      showNotification(`还有${incompleteCount}个子主题未完成`);
    }
  };

  return (
    <div
      className="topic"
      style={{
        backgroundColor: topic.isCompleted ? '#4CAF50' : '#FF5252',
        cursor: 'pointer',
        padding: '10px',
        borderRadius: '4px',
        margin: '5px'
      }}
    >
      <div className="topic-header">
        <input
          type="checkbox"
          checked={topic.isCompleted}
          onChange={handleComplete}
        />
        <span>{topic.title}</span>
      </div>
      {topic.children.length > 0 && (
        <div className="topic-children">
          {topic.children.map(child => (
            <Topic key={child.id} topic={child} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Topic;
