import React from 'react';
import { useDispatch } from 'react-redux';
import { addTopic } from '../store/mindMapSlice';

const Toolbar: React.FC = () => {
  const dispatch = useDispatch();

  const handleAddNode = () => {
    dispatch(addTopic({
      title: '新节点',
      parentId: null // 如果parentId为null，则创建为根节点的子节点
    }));
  };

  return (
    <div className="toolbar" style={{
      padding: '10px',
      borderBottom: '1px solid #eee',
      marginBottom: '20px'
    }}>
      <button
        onClick={handleAddNode}
        style={{
          padding: '8px 16px',
          backgroundColor: '#1976D2',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        添加节点
      </button>
    </div>
  );
};

export default Toolbar;
