import React, { useState, useCallback, useRef } from 'react';
import { useDispatch } from 'react-redux';
import { Topic as TopicType, TopicStyle } from '../types/MindMap';
import {
  toggleTopicComplete,
  connectTopics,
  addTopic,
  updateTopicTitle,
  updateTopicStyle,
  toggleTopicExpand,
  updateTopicType
} from '../store/mindMapSlice';
import { countIncompleteTopics, showNotification } from '../utils/topicUtils';

interface TopicProps {
  topic: TopicType;
  level?: number;
}

const Topic: React.FC<TopicProps> = ({ topic, level = 0 }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(topic.title);
  const [showFormatMenu, setShowFormatMenu] = useState(false);
  const topicRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  const handleComplete = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation();
    dispatch(toggleTopicComplete({
      id: topic.id,
      isCompleted: e.target.checked
    }));

    // 只在取消完成状态时，且有未完成的子任务时显示提示
    if (!e.target.checked && topic.children.length > 0) {
      const incompleteCount = countIncompleteTopics(topic);
      if (incompleteCount > 0) {
        showNotification(`还有${incompleteCount}个子主题未完成`);
      }
    }
  }, [dispatch, topic.id]);

  const handleAddSubTopic = () => {
    dispatch(addTopic({
      title: '新节点',
      parentId: topic.id
    }));
  };

  const handleTitleClick = () => {
    setIsEditing(true);
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditTitle(e.target.value);
  };

  const handleTitleBlur = () => {
    if (editTitle.trim() !== '') {
      dispatch(updateTopicTitle({
        id: topic.id,
        title: editTitle.trim()
      }));
    } else {
      setEditTitle(topic.title);
    }
    setIsEditing(false);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleTitleBlur();
    }
  };

  const handleDragStart = useCallback((e: React.DragEvent) => {
    e.stopPropagation();
    setIsDragging(true);
    e.dataTransfer.setData('text/plain', topic.id);
    e.dataTransfer.effectAllowed = 'move';

    if (topicRef.current) {
      const rect = topicRef.current.getBoundingClientRect();
      e.dataTransfer.setDragImage(topicRef.current, e.clientX - rect.left, e.clientY - rect.top);
    }
  }, [topic.id]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer.types.includes('text/plain')) {
      e.currentTarget.classList.add('drag-over');
      e.dataTransfer.dropEffect = 'move';
    }
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');

    const sourceId = e.dataTransfer.getData('text/plain');
    if (sourceId !== topic.id) {
      dispatch(connectTopics({
        sourceId,
        targetId: topic.id
      }));
    }
  }, [dispatch, topic.id]);

  const handleDragEnd = useCallback(() => {
    setIsDragging(false);
  }, []);

  // 渲染不同类型的节点内容
  const renderTopicContent = () => {
    switch (topic.type) {
      case 'image':
        return (
          <div className="topic-image">
            <img src={topic.content} alt={topic.title} />
          </div>
        );
      case 'link':
        return (
          <a
            href={topic.content}
            target="_blank"
            rel="noopener noreferrer"
            className="topic-link"
          >
            {topic.title}
          </a>
        );
      case 'note':
        return (
          <div className="topic-note">
            <span>{topic.title}</span>
            <p>{topic.content}</p>
          </div>
        );
      default:
        return (
          <span onClick={handleTitleClick}>{topic.title}</span>
        );
    }
  };

  // 处理节点类型更改
  const handleTypeChange = (type: TopicType) => {
    dispatch(updateTopicType({
      id: topic.id,
      type,
      content: type === 'image' ? 'https://placeholder.com/150' : ''
    }));
  };

  // 处理样式更新
  const handleStyleChange = (style: Partial<TopicStyle>) => {
    dispatch(updateTopicStyle({
      id: topic.id,
      style: { ...topic.style, ...style }
    }));
  };

  // 处理折叠/展开
  const handleToggleExpand = (e: React.MouseEvent) => {
    e.stopPropagation();
    dispatch(toggleTopicExpand({ id: topic.id }));
  };

  // 获取节点样式
  const getTopicStyle = () => {
    const style: React.CSSProperties = {
      marginLeft: `${level * 20}px`,
      ...(topic.style && {
        fontSize: topic.style.fontSize,
        color: topic.style.fontColor,
        backgroundColor: topic.style.backgroundColor,
        borderColor: topic.style.borderColor,
        borderStyle: topic.style.borderStyle,
      })
    };
    return style;
  };

  return (
    <div
      ref={topicRef}
      className={`topic ${topic.type} ${topic.isCompleted ? 'completed' : 'incomplete'} ${isDragging ? 'dragging' : ''}`}
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      onDragEnd={handleDragEnd}
      style={getTopicStyle()}
    >
      <div className="topic-header">
        <input
          type="checkbox"
          checked={topic.isCompleted}
          onChange={handleComplete}
        />
        {isEditing ? (
          <input
            type="text"
            value={editTitle}
            onChange={handleTitleChange}
            onBlur={handleTitleBlur}
            onKeyPress={handleKeyPress}
            autoFocus
            className="topic-title-input"
          />
        ) : (
          renderTopicContent()
        )}
        <div className="topic-tools">
          <button
            className="expand-btn"
            onClick={handleToggleExpand}
          >
            {topic.isExpanded ? '−' : '+'}
          </button>
          <button
            className="format-btn"
            onClick={() => setShowFormatMenu(!showFormatMenu)}
          >
            ⚙
          </button>
          <button
            className="add-subtopic-btn"
            onClick={handleAddSubTopic}
          >
            +
          </button>
        </div>
      </div>

      {showFormatMenu && (
        <div className="format-menu">
          <div className="format-section">
            <label>类型:</label>
            <select
              value={topic.type}
              onChange={(e) => handleTypeChange(e.target.value as TopicType)}
            >
              <option value="text">文本</option>
              <option value="image">图片</option>
              <option value="link">链接</option>
              <option value="note">笔记</option>
            </select>
          </div>
          <div className="format-section">
            <label>字体大小:</label>
            <input
              type="number"
              value={topic.style?.fontSize || 16}
              onChange={(e) => handleStyleChange({ fontSize: Number(e.target.value) })}
            />
          </div>
          <div className="format-section">
            <label>颜色:</label>
            <input
              type="color"
              value={topic.style?.fontColor || '#000000'}
              onChange={(e) => handleStyleChange({ fontColor: e.target.value })}
            />
          </div>
          <div className="format-section">
            <label>背景:</label>
            <input
              type="color"
              value={topic.style?.backgroundColor || '#ffffff'}
              onChange={(e) => handleStyleChange({ backgroundColor: e.target.value })}
            />
          </div>
        </div>
      )}

      {topic.isExpanded !== false && topic.children.length > 0 && (
        <div className="topic-children">
          {topic.children.map(child => (
            <Topic key={child.id} topic={child} level={level + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

export default React.memo(Topic);
