import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Topic as TopicType } from '../types/MindMap';
import { toggleTopicComplete, connectTopics, addTopic, updateTopicTitle } from '../store/mindMapSlice';
import { countIncompleteTopics, showNotification } from '../utils/topicUtils';

interface TopicProps {
  topic: TopicType;
}

const Topic: React.FC<TopicProps> = ({ topic }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(topic.title);

  const handleComplete = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation();
    dispatch(toggleTopicComplete({
      id: topic.id,
      isCompleted: e.target.checked
    }));

    // 只在取消完成状态时，且有未完成的子任务时显示提示
    if (!e.target.checked && topic.children.length > 0) {
      const incompleteCount = countIncompleteTopics(topic);
      if (incompleteCount > 0) {
        showNotification(`还有${incompleteCount}个子主题未完成`);
      }
    }
  };

  const handleAddSubTopic = () => {
    dispatch(addTopic({
      title: '新节点',
      parentId: topic.id
    }));
  };

  const handleTitleClick = () => {
    setIsEditing(true);
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditTitle(e.target.value);
  };

  const handleTitleBlur = () => {
    if (editTitle.trim() !== '') {
      dispatch(updateTopicTitle({
        id: topic.id,
        title: editTitle.trim()
      }));
    } else {
      setEditTitle(topic.title);
    }
    setIsEditing(false);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleTitleBlur();
    }
  };

  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData('text/plain', topic.id);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.currentTarget.classList.add('drag-over');
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.currentTarget.classList.remove('drag-over');
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.currentTarget.classList.remove('drag-over');

    const sourceId = e.dataTransfer.getData('text/plain');
    if (sourceId !== topic.id) {
      dispatch(connectTopics({
        sourceId,
        targetId: topic.id
      }));
    }
  };

  return (
    <div
      className={`topic ${topic.isCompleted ? 'completed' : 'incomplete'}`}
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <div className="topic-header">
        <input
          type="checkbox"
          checked={topic.isCompleted}
          onChange={handleComplete}
        />
        {isEditing ? (
          <input
            type="text"
            value={editTitle}
            onChange={handleTitleChange}
            onBlur={handleTitleBlur}
            onKeyPress={handleKeyPress}
            autoFocus
            className="topic-title-input"
          />
        ) : (
          <span onClick={handleTitleClick}>{topic.title}</span>
        )}
        <div className={`completion-status ${topic.isCompleted ? 'complete' : 'incomplete'}`}>
          {topic.isCompleted ? '已完成' : '未完成'}
        </div>
        <button className="add-subtopic-btn" onClick={handleAddSubTopic}>
          <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M8 1V15M1 8H15" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
          </svg>
        </button>
      </div>
      {topic.children.length > 0 && (
        <div className="topic-children">
          {topic.children.map(child => (
            <Topic key={child.id} topic={child} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Topic;
