import React, { useState, useCallback, useRef } from 'react';
import { useDispatch } from 'react-redux';
import { Topic as TopicType } from '../types/MindMap';
import { toggleTopicComplete, connectTopics, addTopic, updateTopicTitle } from '../store/mindMapSlice';
import { countIncompleteTopics, showNotification } from '../utils/topicUtils';

interface TopicProps {
  topic: TopicType;
  level?: number;
  angle?: number;
}

const Topic: React.FC<TopicProps> = ({ topic, level = 0, angle = 0 }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(topic.title);
  const topicRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  const handleComplete = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation();
    dispatch(toggleTopicComplete({
      id: topic.id,
      isCompleted: e.target.checked
    }));

    // 只在取消完成状态时，且有未完成的子任务时显示提示
    if (!e.target.checked && topic.children.length > 0) {
      const incompleteCount = countIncompleteTopics(topic);
      if (incompleteCount > 0) {
        showNotification(`还有${incompleteCount}个子主题未完成`);
      }
    }
  }, [dispatch, topic.id]);

  const handleAddSubTopic = () => {
    dispatch(addTopic({
      title: '新节点',
      parentId: topic.id
    }));
  };

  const handleTitleClick = () => {
    setIsEditing(true);
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditTitle(e.target.value);
  };

  const handleTitleBlur = () => {
    if (editTitle.trim() !== '') {
      dispatch(updateTopicTitle({
        id: topic.id,
        title: editTitle.trim()
      }));
    } else {
      setEditTitle(topic.title);
    }
    setIsEditing(false);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleTitleBlur();
    }
  };

  const handleDragStart = useCallback((e: React.DragEvent) => {
    e.stopPropagation();
    setIsDragging(true);
    e.dataTransfer.setData('text/plain', topic.id);
    e.dataTransfer.effectAllowed = 'move';

    if (topicRef.current) {
      const rect = topicRef.current.getBoundingClientRect();
      e.dataTransfer.setDragImage(topicRef.current, e.clientX - rect.left, e.clientY - rect.top);
    }
  }, [topic.id]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer.types.includes('text/plain')) {
      e.currentTarget.classList.add('drag-over');
      e.dataTransfer.dropEffect = 'move';
    }
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    e.currentTarget.classList.remove('drag-over');

    const sourceId = e.dataTransfer.getData('text/plain');
    if (sourceId !== topic.id) {
      dispatch(connectTopics({
        sourceId,
        targetId: topic.id
      }));
    }
  }, [dispatch, topic.id]);

  const handleDragEnd = useCallback(() => {
    setIsDragging(false);
  }, []);

  // 计算子节点的角度分布
  const getChildAngles = (childCount: number) => {
    const angleStep = 60 / (childCount + 1);  // 60度范围内平均分布
    const startAngle = angle - 30;  // 从-30度开始
    return Array(childCount).fill(0).map((_, i) => startAngle + (i + 1) * angleStep);
  };

  // 计算节点位置
  const getNodePosition = () => {
    if (level === 0) return {};
    const distance = 180;  // 每层节点之间的距离
    const x = Math.cos((angle * Math.PI) / 180) * distance * level;
    const y = Math.sin((angle * Math.PI) / 180) * distance * level;
    return {
      transform: `translate(${x}px, ${y}px)`,
    };
  };

  return (
    <div
      ref={topicRef}
      className={`topic ${topic.isCompleted ? 'completed' : 'incomplete'} ${isDragging ? 'dragging' : ''}`}
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      onDragEnd={handleDragEnd}
      style={getNodePosition()}
    >
      {level > 0 && (
        <div
          className="connection-line"
          style={{
            width: '2px',
            height: '100%',
            position: 'absolute',
            background: '#cbd5e0',
            transformOrigin: '0 0',
            transform: `rotate(${angle}deg)`,
            left: '50%',
            top: '50%',
          }}
        />
      )}
      <div className="topic-header">
        <input
          type="checkbox"
          checked={topic.isCompleted}
          onChange={handleComplete}
        />
        {isEditing ? (
          <input
            type="text"
            value={editTitle}
            onChange={handleTitleChange}
            onBlur={handleTitleBlur}
            onKeyPress={handleKeyPress}
            autoFocus
            className="topic-title-input"
          />
        ) : (
          <span onClick={handleTitleClick}>{topic.title}</span>
        )}
        <div className={`completion-status ${topic.isCompleted ? 'complete' : 'incomplete'}`}>
          {topic.isCompleted ? '已完成' : '未完成'}
        </div>
        <button className="add-subtopic-btn" onClick={handleAddSubTopic}>
          <svg width="12" height="12" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M8 1V15M1 8H15" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
          </svg>
        </button>
      </div>
      {topic.children.length > 0 && (
        <div className="topic-children" style={{ position: 'relative' }}>
          {topic.children.map((child, index) => (
            <Topic
              key={child.id}
              topic={child}
              level={level + 1}
              angle={getChildAngles(topic.children.length)[index]}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default React.memo(Topic);
